import Settings from './settings.js';

export const makeInitialState = function() {
	const blocksMap = [];
	for (let i = 0; i < Settings.stageSize.height; i++) {
		blocksMap.push(Array(10));
	}
	console.log(blocksMap[0].length);
	return {
		movableBlocks : [],
		blocksMap: blocksMap,
	}
}

