import React from 'react';
import logo from './logo.svg';
import Settings from './settings.js';
import './App.css';

function App() {
  return (
    <div className="App">
      <GameStage />
    </div>
  );
}

function getRealSize(logicSize) {
  return logicSize * Settings.cellSize
}

class GameStage extends React.Component {
  constructor(props) {
    super(props);
    this.style = {
      width: getRealSize(9) + 'px',
      height: getRealSize(20) + 'px'
    }
  }

  render() {
    return (
      <div className={'Tetris-stage'} style={this.style}>
        <TetrisBlock y={19} x = {7} />
        <TetrisBlock y={19} x = {8} />
      </div>
    )
  }
}

function TetrisBlock(props) {
  const style = {
    top: getRealSize(props.y) + 'px',
    left: getRealSize(props.x) +'px',
    position: 'absolute',
    width: Settings.cellSize - 4 + 'px',
    height: Settings.cellSize - 4 + 'px',
    border: '2px solid white',
    backgroundColor: '#ff0000'
  };
  return <div className={'Block'} style={style}> </div>
}

export default App;
