import Settings from './settings.js';

const makeInitialState = function() {
	const blocksMap = [];
	for (let i = 0; i < Settings.stageSize.height; i++) {
		blocksMap.push(Array(10));
	}
	return {
		movableBlocks : [],
		blocksMap: blocksMap,
	}
}

export default makeInitialState;
