import React from 'react';
import Settings from './settings.js';
import {makeInitialState, moveBlocksDown} from './state.js';
import './App.css';

function App() {
  return (
    <div className="App">
      <GameStage />
    </div>
  );
}

const keyCodes = [37, 38, 39, 40];

class GameStage extends React.Component {
  constructor(props) {
    super(props);

    this.stageWidth = Settings.stageSize.width;
    this.stageHeight = Settings.stageSize.height;
    this.style = {
      width: addPx(getRealSize(this.stageWidth)),
      height: addPx(getRealSize(this.stageHeight))
    }

    this.state = makeInitialState();

    this._handleKeyDown = this._handleKeyDown.bind(this);
    this._makeRegularMove = this._makeRegularMove.bind(this);
  }

  _handleKeyDown(event) {
    console.log(event.keyCode);
    switch( event.keyCode ) {
        case 27:
            this.setState(state => ({
              position : {x: state.position.x, y: state.position.y + 1}
            }));
            break;
        default: 
            break;
    }
  }

  _makeRegularMove() {
    this.setState(moveBlocksDown);
  }

  componentDidMount() {
    document.addEventListener("keydown", this._handleKeyDown);
    this.timerId = setInterval(this._makeRegularMove, Settings.updateRate);
  }

  componentWillUnmount() {
    document.removeEventListener("keydown", this._handleKeyDown);
    clearInterval(this.timerID);
  }

  render() {
    return (
      <div className={'Tetris-stage'} style={this.style}>
        {state.movableBlocks.map(block => <TetrisBlock position={block.position} color={block.color} />)}
      </div>
    )
  }
}

function TetrisBlock(props) {
  console.log('rendering is happened');
  const style = {
    position: 'absolute',
    top: addPx(getRealSize(props.position.y)),
    left: addPx(getRealSize(props.position.x)),
    backgroundColor: props.color,
    border: '2px solid white',
    width : addPx(Settings.cellSize - 4),
    height : addPx(Settings.cellSize - 4),
  }
  return (
    <div className={'Block'} style={style}> </div>
  )
}

export default App;

function getRealSize(logicSize) {
  return logicSize * Settings.cellSize
}

function addPx(value) {
  return value + 'px';
}
