import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <GameStage />
    </div>
  );
}

class GameStage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      rotationClass: 'App-logo-right'
    };
    // this.handleClick = this.handleClick.bind(this)
  }

  render() {
    return (
      <div className={'Tetris-stage'}>
        <TetrisBlock y={15} x = {10} />
      </div>
    )
  }
}

function TetrisBlock(props) {
  const style = {
    top: props.y + 'vh',
    left: props.x +'vw',
    position: 'absolute',
    width: '20px',
    height: '20px',
    backgroundColor: '#ff0000'
  };
  return <div className={'Block'} style={style}> </div>
}

export default App;
