import constants from './constants';
import Settings from './settings';
import C from 'constants'

export class TetrisFigure {
	constructor(startPosition, rotation, color='#00ff00') {
		this.position = this.ensureInside(startPosition, rotation);
		this.rotation = rotation;
		this.color = color
	}

	rotate() {
		return new this.constructor(this.position, (this.rotation + 1) % 4, this.color)
	}

	move(newPosition) {
		return new this.constructor(newPosition, this.rotation, this.color)
	}

	getNextFigure(action) {
		switch (action) {
			case 'left':

				break;

			default:
				break;
		}
	}


	get blocks() {
		switch (this.rotation) {
			case 0:
				return [
					{ ...this.position },
					{ ...this.position, x: this.position.x - 1 },
					{ ...this.position, x: this.position.x + 1 },
					{ ...this.position, y: this.position.y - 1 }
				];
			case 1:
				return [
					{ ...this.position },
					{ ...this.position, x: this.position.x + 1 },
					{ ...this.position, y: this.position.y - 1 },
					{ ...this.position, y: this.position.y + 1 }
				]
			case 2:
				return [
					{ ...this.position },
					{ ...this.position, x: this.position.x - 1 },
					{ ...this.position, x: this.position.x + 1 },
					{ ...this.position, y: this.position.y + 1 }
				]
			case 3:
				return [
					{ ...this.position },
					{ ...this.position, x: this.position.x - 1 },
					{ ...this.position, y: this.position.y - 1 },
					{ ...this.position, y: this.position.y + 1 }
				]
			default: return []
		}
	}

	ensureInside({x, y}, rotation) {
		return {
			x: Math.max(1, Math.min(x, Settings.stageSize.width - 2)),
			y: Math.max(1, Math.min(y, Settings.stageSize.height - (rotation == 0 ? 1 : 2)))
		}
	}

}


export function getMovableBlock() {
	return new TetrisFigure({x: 1, y: 4}, 0);
}
