import React from 'react';
import logo from './logo.svg';
import Settings from './settings.js';
import './App.css';

function App() {
  return (
    <div className="App">
      <GameStage />
    </div>
  );
}


class GameStage extends React.Component {
  constructor(props) {
    super(props);
    this.style = {
      width: getRealSize(9) + 'px',
      height: getRealSize(20) + 'px'
    }
  }

  render() {
    return (
      <div className={'Tetris-stage'} style={this.style}>
        <TetrisBlock Position={{x:8, y:7}}> </TetrisBlock>
      </div>
    )
  }
}

class TetrisBlock extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      position: props.Position
    };
    this.style = {
      position: 'absolute',
      top: addPx(getRealSize(this.state.position.y)),
      left: addPx(getRealSize(this.state.position.x)),
      width: addPx(Settings.cellSize - 4),
      height: addPx(Settings.cellSize - 4),
      border: '2px solid white',
      backgroundColor: '#ff0000'
    };
  }

  render() {

    return (
      <div className={'Block'} style={this.style}> </div>
    )

  }
}

export default App;

function getRealSize(logicSize) {
  return logicSize * Settings.cellSize
}

function addPx(value) {
  return value + 'px';
}
